                old.parent.insertBefore(avatar, old.nextSibling);
                $avatar.removeClass('dragging');
                avatar.style.position = old.position;
                avatar.style.left = old.left;
                avatar.style.top = old.top;
                avatar.style.zIndex = old.zIndex;
