                pobox: $this.find('POBOX').text().trim(),
                extadd: $this.find('EXTADR').text().trim(),
                street: $this.find('STREET').text().trim(),
                locality: $this.find('LOCALITY').text().trim(),
                region: $this.find('REGION').text().trim(),
