            this.quill.container.firstChild.classList.add('rich-textarea');
            this.contact = this.view.contact;
            this.account = this.view.account;
            this.fwd_messages = [];
            this.edit_message = null;
            this.$('.account-jid').text(this.account.get('jid'));
            this.updateAvatar();
