                                if (device.get('ik') === null) {
                                    counter--;
                                    if (!counter) {
                                        if (Object.keys(peer.devices).length === 1)
                                            is_trusted = 'nil';
                                        dfd.resolve(is_trusted);
                                    }
                                    continue;
                                }
